mapper.select:
select *
from
(
	select rownum as RNUM, A.*
	from
	(
		select no,title,writer,content,regdate,fileName,fileSize,originalFilename,(sysdate-regdate)*24 as dateTerm
		from event order by no desc


	)A
)

where RNUM>#{firstRecordIndex} 
	and RNUM<=#{firstRecordIndex}+ #{recordCountPerPage}

mapper.update:
	update event
	set title=#{title},content=#{content}

	,fileName=#{fileName},fileSize=#{fileSize},originalFileName=#{originalFileName}

where no=#{no}
